import React,{useState,useEffect} from 'react';
import {useNavigate, useParams, Link} from 'react-router-dom';
import "./AddEdit.css";
import axios from 'axios';
import {toast} from 'react-toastify';


const initialState = {
	task: "",
	ls_date: "",
};

const AddEdit = () => {

const navigate = useNavigate();

//const [task,setTask]=useState('');     
//const [ls_date,setLs_date]=useState('');

const [state,setState] = useState(initialState);

const [task,ls_date] = state;

const history = useNavigate();

const {t} = useParams();

useEffect(() => {
	axios.get(`http://localhost:5000/get/${t}`).then((resp) => setState({ ...resp.data[0]  }));
},[t]);

const handleSubmit = (e) => {
	e.preventDefault();
	if(!task || !ls_date){
	toast.error("Please provide value into each input field");
	}else{if(!t){
	axios.post(`http://localhost:5000/api/post`,{
	task,
	ls_date
	}).then(() => {
	setState({task: "", ls_date: ""});
	}).catch((err) => toast.error(err.response.data));
	toast.success("Tasks added successfully");
	}else {
        axios.post(`http://localhost:5000/api/update/${task}`,{
        task,
        ls_date
        }).then(() => {
        setState({task: "", ls_date: ""});
        }).catch((err) => toast.error(err.response.data));
        toast.success("Tasks added successfully");

	}
	setTimeout(() => navigate("/"),500);
	}
};

const handleInputChange = (e) => {
	const {item,value} = e.target;
	setState({ ...state, [item]: value})
}


return (	
<div style={{marginTop:"100px"}}>
<form style={{
margin: "auto",
padding: "15px",
maxWidth: "300px",
alignContent: "left"
}}
onSubmit={handleSubmit}
>
<label htmlFor="task">Task  : </label>
<input type="text" id="task" placeholder="Task..." value={task||""} onChange={e => handleInputChange(e.target.value)} />
<label htmlFor="ls_date">Last Date : </label>
<input type="date" id="ls_date" value={ls_date||""} onChange={e => handleInputChange(e.target.value)} />

<input type="submit" value={!t ? "Update" : "Save"} />
<Link to ="/">
<input type="button" value="Go Back" />
</Link>
</form>
</div>
);
}

export default AddEdit;
